import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Login from './components/Login';
import Home from './components/Home';
import Courses from './components/Courses';
import ExamSchedule from './components/ExamSchedule';
import Faculty from './components/Faculty';
import Timetable from './components/Timetable';
import Contact from './components/Contact';

const App = () => {
  // Use useLocation to get the current path
  const location = useLocation();

  // Check if the current route is the login page
  const isLoginPage = location.pathname === "/";

  // Simulate a login check (you should replace this with an actual check, e.g., from localStorage or state)
  const isAuthenticated = false; // Replace with your actual auth check logic

  return (
    <Router>
      {/* Conditionally render Navbar: hide it on the login page */}
      {!isLoginPage && <Navbar />}

      <div className="main-content">
        <Routes>
          {/* Redirect user to Home page if already authenticated */}
          <Route
            path="/"
            element={isAuthenticated ? <Navigate to="/home" /> : <Login />}
          />

          {/* All the other routes */}
          <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate to="/" />} />
          <Route path="/courses" element={isAuthenticated ? <Courses /> : <Navigate to="/" />} />
          <Route path="/exam-schedule" element={isAuthenticated ? <ExamSchedule /> : <Navigate to="/" />} />
          <Route path="/faculty" element={isAuthenticated ? <Faculty /> : <Navigate to="/" />} />
          <Route path="/timetable" element={isAuthenticated ? <Timetable /> : <Navigate to="/" />} />
          <Route path="/contact" element={isAuthenticated ? <Contact /> : <Navigate to="/" />} />
        </Routes>
      </div>

      {/* Conditionally render Footer: hide it on the login page */}
      {!isLoginPage && <Footer />}
    </Router>
  );
};

export default App;
